import tkinter as tk
from PIL import Image, ImageTk
import requests
from io import BytesIO

def cargar_imagen_desde_url(url):
    response = requests.get(url)
    imagen = Image.open(BytesIO(response.content))
    return imagen

ventana = tk.Tk()
ventana.title("Imagen de Internet")

url_imagen = "https://www.ikea.com/es/es/images/products/ostano-silla-marron-rojizo-remmarn-marron-rojizo__1120081_pe873713_s5.jpg?"  # Reemplaza con la URL de la imagen que desees mostrar

imagen = cargar_imagen_desde_url(url_imagen)
imagen_tk = ImageTk.PhotoImage(imagen)

label = tk.Label(ventana, image=imagen_tk)
label.pack()

ventana.mainloop()
